INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('ef40c85d0e72b1c1ab5de4d35ab4efc5','Meganef4', '{2015-10-18 11:46:40-07:00}', '3', '1419700251', 'The book was interesting, unique, and strange. I wasnt in love with the storyline. The front cover kind of reminded me of a Harry Potter like magical story. It was magical alright, but it didnt live up to my expectations. However, the author is very creative with his word usage which is what made it a page-turner for me. It wasnt stale or typical, just different and strange.'); 
