INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('a0528c56965a4b82cb1fdcd472fa6b88','Melindaa05', '{2015-04-21 14:43:18-07:00}', '4', '1419700251', 'A little bit fantasy, a little steampunk, fantastical and humorous writing, and packed with adventure, this book offered a unique new voice in childrens literature.'); 
INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('b23d2fb3bd4c1a58fdcb30247eb1496d','Fernandob23', '{2013-03-24 05:50:29-07:00}', '5', '1419700251', 'Very imaginative and well written. Peter is a believable character for whom you both feel sympathy and want to cheer on through to the end. I love the irreverence in how he was abandoned as a baby and raised himself. There is great wit for adult readers that kids may or may not perceive.'); 
