INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('bff0264b52daee08c04926f5773af18a','Williambff', '{2012-04-23 06:08:19-07:00}', '4', '1419700251', 'Really liked the beginning of this book, and was telling the boys it was one they should read.... but the ending didnt live up to the promise of the beginning. Still liked it, but didnt love it. Really 3 1/2 stars for me.'); 
