INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('f8be98946790008f2cdd39b435d6f7ac','Richardf8b', '{2015-03-09 10:00:57-07:00}', '5', '0140386696', 'They dont write em like this anymore... Well, sometimes they do, but this has a nostalgic feel to it, for me as a child of the 80s, growing up with Ahlberg stories. Intended for older readers than Each Peach and Funnybones, this is a chapter book story about a boy who, somehow, suddenly finds himself turning into a dog. 
