INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('60f516cd7890a0d4f9a6b0bb758042f9','Kayla60f', '{2011-12-05 16:18:31-08:00}', '3', '0698114272', 'Whenever Bootsie Barker comes over with her mother all she wants to do is play mean games where she is a carnivorous dinosaur. This causes the other young girl a lot of stress until she figures out how to play Bootsies game: she will become a Paleontologist that hunts for dinosaur bones! Suddenly Bootsie doesnt feel like visiting anymore.'); 
