INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('24f62f65c44a4263e57b541b192c2bfd','John24f', '{2013-02-26 09:40:34-08:00}', '3', '0545307406', 'Sweetly illustrated, this book shows pairs of inanimate objects with smiling faces (cookies and milk, bacon and eggs, two mittens, a fork and a spoon, hot chocolate and marshmallows) that go together just like the reader and baby.'); 
