INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('03e1a31794dbe42245b6baa903ab0e0f','Linda03e', '{2012-12-30 11:48:45-08:00}', '3', '0803738048', 'A cozy, visually rich celebration of Christmas to pore over Readers are off to the North Pole in this brimming holiday treat. Readers will be pulled into a nostalgic, exquisitely detailed moment of the yuletide journey: a trip in Santas sleigh, supper with Mr. and Mrs. Claus, watching the elves hard at work, and finally arriving home in time for Christmas morning. Secret clues, plus hidden allusions to classic Christmas carols, elevate this from a gorgeous book to an interactive holiday treasure hunt; a natural fit for the whole family at Christmastime.'); 
