INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('b7e00d937b218a4f1f3b1f6b1427def5','Stephanieb7e', '{2013-01-17 21:07:14-08:00}', '5', '0803737513', 'I loved, loved, loved this book! The illustrations are what make it a perfect book for all children to read. The words exactly describe the pictures and they even rhyme! It teaches kids to use their imaginations and it shows creative ways to do things (like having a jet dryer to dry you off after your shower). It is creative, funny and entertaining!'); 
