INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('b51313eaa8442a9ac4f1e035941302d7','Normab51', '{2013-05-06 13:44:29-07:00}', '5', '0316056146', 'Funke and I had the same tour guide in Salisbury which led us to write in one way or another about Ela of Salisbury. Even though this is a YA title, both of us adults in our household enjoyed it on Kindle. Then we bought a hard cover for our Grandsons birthday.'); 
