INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('91aba9837389af618e053244280136c9','Lisa91a', '{2016-03-03 08:25:12-08:00}', '3', '1585369586', 'This book goes through a food chain in a marsh in the meadow. Each page adds onto the last--the eaglets get fed by the eagle who catches the fish who swallow the tadpoles and on and on. Great for repetition. 
INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('5f28aa0cb00b048787811e92f6bb6c3e','Heather5f2', '{2016-04-19 13:51:27-07:00}', '5', '1585369586', 'This is a lovely read-aloud for a child. Jeanies language is engaging and fun, and the illustrations draw a young reader right into the thriving life cycle of a marsh.'); 
INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('1db3a474000915ffffc8f8c07f199ba3','Billy1db', '{2016-11-22 09:25:42-08:00}', '3', '1585369586', 'A fun way to introduce food chains to primary students that engages and promotes comprehension and reader connections. A cumulative tale with illustrations that bring life to what could have been a dry subject. 
