INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('66b0d9d3ad5faf06c0045658531c3309','Brian66b', '{2016-08-16 11:57:10-07:00}', '4', '1585369438', 'A look at the life and the clever defenses of a walking stick insect. Main text appears larger and is perfect for reading aloud, supporting text appears smaller and off to the side in each spread and gives more information about the main fact of each spread as well as gentle reminders for how best to enjoy nature. 
INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('f900f701a8b3da08b00a698a620d5018','Kylef90', '{2016-08-01 15:01:42-07:00}', '4', '1585369438', 'So many interesting facts! A story for shorter attention spans with more info for kids with longer ones. Great illustrations!'); 
INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('9ad644f2064317dd5b4e5c5dab48d435','Brad9ad', '{2016-12-18 14:02:43-08:00}', '5', '1585369438', 'Follow a single walking stick throughout this book and learn all sorts of amazing things, like how ants help walking stick eggs survive the winter. And how walking sticks protect themselves against squirrels and birds. And how a female walking stick can lay hatchable eggs, even without a male stick. A poetic, dramatic main text, great for reading aloud, is supplemented by longer sidebars giving more scientific detail. Awesome for storytime and science class!'); 
INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('158a9de5732cb708457ad80e9af1d591','Jose158', '{2016-03-01 12:19:07-08:00}', '5', '1585369438', 'This should be added to every nature bookshelf, but also serves as mentor-text for expository science content and figurative narrative text, as well. Kids will love this, but so will adults.'); 
