INSERT INTO schema_08.reviews_08(review_id, nickname, date_created, review_score, book_isbn, description) VALUES('7927b5138bc249be990962f8cd63412d','Rhonda792', '{2016-09-09 12:38:45-07:00}', '4', '1585369438', 'This was a fantastic book that I believe adults and children alike will appreciate. I enjoyed the illustrations coupled with the story line. Not only did the story line itself provide factual information, but the snippets provided towards the bottom of each page provided additional and more detailed information.'); 
